<?phpnamespace App\Http\Controllers;use Illuminate\Support\Facades\Auth;use Illuminate\Http\Request;class LoginController extends Controller{ function index(){ return view('/Login');} public function Login( Request $req){ $valid=$req->validate([ 'email'=>'required|email', 'password'=>'required ' ]); if (Auth::attempt($valid)){ $req->session()->regenerate(); return redirect()->intended('/dashboard');} return back()->withErrors([ 'email'=>'The provided credentials do not match our records.', ])->onlyInput('email');}}